    public static ArrayList<Movie> getMovieList() {
        ArrayList<Movie> movies = new ArrayList<>();
        for (String[] mData : data) {
            Movie movie = new Movie();
            movie.setTitle(mData[0]);
            movie.setDesc(mData[1]);
            movie.setPhoto(mData[2]);
            movies.add(movie);
        }
        return movies;
    }

